    private void llenarTabla() {
        try {
            ArrayList<String> mundialesTemp = new GestorGrupo().listarGrupos();
            for (String mdl : mundialesTemp) {
                String[] datos;
                datos = mdl.split(",");

                DefaultTableModel modelo = (DefaultTableModel) tblListaGrupos.getModel();
                modelo.addRow(datos);
            }
        } catch (Exception e) {
            try {
                throw e;
            } catch (Exception ex) {
                Logger.getLogger(UIListaMundialesAdmin.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }

    private void regresar() {
        UIPrincipalAdmin ui = new UIPrincipalAdmin();
        ui.setVisible(true);
        this.setVisible(false);
    }

